mapper.select:
SELECT
FROM movie m JOIN boxoffice b ON m.movie_id = b.movie_id WHERE m.movie_id = #{movieId, jdbcType=VARCHAR}
ORDER BY b.movie_rank

mapper.insert:

	INSERT INTO boxoffice (
	    movie_id,
	    movie_rank
	) VALUES (
	    (SELECT movie_id
	     from movie
	     WHERE kortitle LIKE '%'|| #{kortitle} || '%'
	     AND rel_date = #{relDate, jdbcType=VARCHAR}),
	    #{movieRank, jdbcType=VARCHAR}
	)
