mapper.select:
SELECT T1.*,T2.* FROM ( SELECT B.NOTICE_ID as noticeId, B.TITLE, B.ORG_FILE_NM as orgFileNm, B.SAVE_FILE_NM as saveFileNm, B.EXT, B.CONTENTS, B.WRITER_ID as writerId, B.MOD_DT as modDt, B.REG_DT as regDt, B.rnum as num, B.BRANCH_NM as branchSNm FROM ( SELECT ROWNUM AS rnum, A.* FROM ( SELECT * FROM notice WHERE 1=1 AND reg_dt > to_date('19700101','yyyymmdd')
ORDER BY reg_dt DESC )A WHERE rownum <= (#{pageSize} * (#{pageNum}-1)+#{pageSize}) ) B WHERE B.rnum >= (#{pageSize} * (#{pageNum}-1)+1) )T1 NATURAL JOIN ( SELECT COUNT(*) totalCnt FROM notice WHERE 1=1
)T2

mapper.update:
UPDATE notice
SET  READ_CNT = (SELECT NVL(MAX(READ_CNT),0)+1 FROM notice WHERE NOTICE_ID = #{noticeId,jdbcType=VARCHAR})
WHERE NOTICE_ID = #{noticeId,jdbcType=VARCHAR}

UPDATE notice
SET   title = #{title,jdbcType=VARCHAR}
	, org_file_nm = #{orgFileNm,jdbcType=VARCHAR}
	, save_file_nm = #{saveFileNm,jdbcType=VARCHAR}
	, ext = #{ext,jdbcType=VARCHAR}
	, contents = #{contents,jdbcType=VARCHAR}
	, writer_id = #{writerId,jdbcType=VARCHAR}
	, BRANCH_NM = #{branchSNm,jdbcType=VARCHAR}
	, MOD_DT = SYSDATE
	, REG_DT = SYSDATE
WHERE notice_id = #{noticeId,jdbcType=VARCHAR}
