mapper.select:
SELECT T1.*,T2.* FROM ( SELECT B.USER_ID as userId , B.QNA_NUM as qnaNum , B.TITLE as title , B.CONTENTS as contents , B.STATUS as status , CASE TO_CHAR(B.reg_dt, 'yyyy.mm.dd') when TO_CHAR(sysdate, 'yyyy.mm.dd') THEN TO_CHAR(sysdate, 'HH24:MI') ELSE TO_CHAR(B.reg_dt, 'yyyy.mm.dd') END AS regDt , B.ORG_FILE_NM as orgFileNm , B.SAVE_FILE_NM as saveFileNm , B.EXT as ext , B.FILE_SIZE as fileSize , B.RE_CONTENTS as reContents , B.rnum as num , CASE TO_CHAR(B.re_dt, 'yyyy.mm.dd') when TO_CHAR(sysdate, 'yyyy.mm.dd') THEN TO_CHAR(sysdate, 'HH24:MI') ELSE TO_CHAR(B.re_dt, 'yyyy.mm.dd') END AS reDt FROM ( SELECT ROWNUM AS rnum, A.* FROM ( SELECT * FROM qna WHERE 1=1 AND reg_dt > to_date('19700101','yyyymmdd')
ORDER BY reg_dt DESC )A WHERE rownum <= (#{pageSize} * (#{pageNum}-1)+#{pageSize}) ) B WHERE B.rnum >= (#{pageSize} * (#{pageNum}-1)+1) )T1 NATURAL JOIN ( SELECT COUNT(*) totalCnt FROM qna WHERE 1=1
)T2

mapper.update:
UPDATE qna
SET  READ_CNT = (SELECT NVL(MAX(READ_CNT),0)+1 FROM qna WHERE USER_ID = #{userId,jdbcType=VARCHAR})
WHERE USER_ID = #{userId,jdbcType=VARCHAR}

UPDATE qna
SET   
	  TITLE        = #{title   ,jdbcType=VARCHAR}
	, CONTENTS     = #{contents,jdbcType=VARCHAR}
	, STATUS       = #{status  ,jdbcType=VARCHAR}
	, REG_DT       = SYSDATE
	, ORG_FILE_NM  = #{orgFileNm ,jdbcType=VARCHAR}
	, SAVE_FILE_NM = #{saveFileNm,jdbcType=VARCHAR}
	, EXT          = #{ext       ,jdbcType=VARCHAR}
	, FILE_SIZE    = #{fileSize  ,jdbcType=VARCHAR}
	, RE_CONTENTS  = #{reContents,jdbcType=VARCHAR}
	, RE_DT        = SYSDATE
WHERE USER_ID = #{userId,jdbcType=VARCHAR}
AND QNA_NUM = #{qnaNum,jdbcType=VARCHAR}

UPDATE qna
SET   
	  QNA_NUM      = #{qnaNum  ,jdbcType=VARCHAR}
	, TITLE        = #{title   ,jdbcType=VARCHAR}
	, CONTENTS     = #{contents,jdbcType=VARCHAR}
	, STATUS       = #{status  ,jdbcType=VARCHAR}
	, ORG_FILE_NM  = #{orgFileNm ,jdbcType=VARCHAR}
	, SAVE_FILE_NM = #{saveFileNm,jdbcType=VARCHAR}
	, EXT          = #{ext       ,jdbcType=VARCHAR}
	, FILE_SIZE    = #{fileSize  ,jdbcType=VARCHAR}
	, RE_CONTENTS  = #{reContents,jdbcType=VARCHAR}
	, RE_DT        = SYSDATE
WHERE USER_ID = #{userId,jdbcType=VARCHAR}

mapper.insert:
INSERT INTO qna (
	  USER_ID
	, TITLE       
	, CONTENTS    
	, STATUS      
	, REG_DT      
	, ORG_FILE_NM 
	, SAVE_FILE_NM
	, EXT         
	, FILE_SIZE   
	, RE_CONTENTS 
	, RE_DT 
) VALUES (
	  #{userId  ,jdbcType=VARCHAR}
	, #{title   ,jdbcType=VARCHAR}
	, #{contents,jdbcType=VARCHAR}
	, #{status  ,jdbcType=VARCHAR}
	, SYSDATE
	, #{orgFileNm ,jdbcType=VARCHAR}
	, #{saveFileNm,jdbcType=VARCHAR}
	, #{ext       ,jdbcType=VARCHAR}
	, #{fileSize  ,jdbcType=VARCHAR}
	, #{reContents,jdbcType=VARCHAR}
	, SYSDATE
)
