mapper.update:
UPDATE SEAT
SET
	USE_YN = #{useYN, jdbcType=NUMERIC}
WHERE ROOM_ID = #{roomId, jdbcType=VARCHAR}
AND SEAT_NM = #{seatNm, jdbcType=VARCHAR}

UPDATE SEAT_RESERVATION
SET
	USE_YN = #{useYN, jdbcType=NUMERIC}
WHERE ROOM_ID = #{roomId, jdbcType=VARCHAR}
AND SEAT_NM = #{seatNm, jdbcType=VARCHAR}
