mapper.select:
SELECT T.RNUM as num ,T.coupon_nm as coupon_nm ,T.coupon_code as coupon_code ,to_char(T.use_dt,'YYYY-MM-DD') as use_dt ,T.usable as usable FROM( SELECT ROWNUM AS RNUM, A.* FROM( SELECT coupon_nm, coupon_code, usable, use_dt FROM user_coupon
WHERE user_id=#{user_id,jdbcType=VARCHAR}
)A WHERE CEIL(ROWNUM/ #{pageSize, jdbcType=VARCHAR} ) = #{pageNum, jdbcType=VARCHAR} )T

SELECT T.RNUM as num ,T.title as title ,T.status as status ,T.reg_dt as reg_dt FROM( SELECT ROWNUM AS RNUM, A.* FROM( SELECT title, status, reg_dt FROM qna
WHERE user_id=#{user_id,jdbcType=VARCHAR}
)A WHERE CEIL(ROWNUM/ #{pageSize, jdbcType=VARCHAR} ) = #{pageNum, jdbcType=VARCHAR} )T

SELECT T.RNUM as num ,T.pay_code as pay_code ,T.product_nm as product_nm ,to_char(T.total_cost,'999,999,999')as total_cost ,to_char(T.pay_dt,'YYYY-MM-DD') as pay_dt FROM( SELECT ROWNUM AS RNUM, A.* FROM( SELECT b.pay_code, a.product_nm, b.total_cost, b.pay_dt FROM PRODUCT a JOIN PAY_HISTORY b
ON a.product_id =b.product_id WHERE user_id=#{user_id,jdbcType=VARCHAR}
)A WHERE CEIL(ROWNUM/ #{pageSize, jdbcType=VARCHAR} ) = #{pageNum, jdbcType=VARCHAR} )T
