mapper.select:
SELECT T.RNUM as num ,T.coupon_nm as coupon_nm ,T.coupon_code as coupon_code ,T.use_dt as use_dt ,T.usable as usable FROM( SELECT ROWNUM AS RNUM, A.* FROM( SELECT coupon_nm, coupon_code, usable, use_dt FROM user_coupon
WHERE user_id=#{user_id,jdbcType=VARCHAR}
)A WHERE CEIL(ROWNUM/ #{pageSize, jdbcType=VARCHAR} ) = #{pageNum, jdbcType=VARCHAR} )T

SELECT T.RNUM as num ,T.title as title ,T.status as status ,T.reg_dt as reg_dt FROM( SELECT ROWNUM AS RNUM, A.* FROM( SELECT title, status, reg_dt FROM qna
WHERE user_id=#{user_id,jdbcType=VARCHAR}
)A WHERE CEIL(ROWNUM/ #{pageSize, jdbcType=VARCHAR} ) = #{pageNum, jdbcType=VARCHAR} )T
